package com.jancar.settings.contract;import android.os.Parcel;import android.os.Parcelable;/** * Created by ouyan on 2018/9/10. */public class NavigationEntity implements Parcelable {    private String name;    private int  img;    private String packageName;    private String className;    private boolean isUp;    public NavigationEntity(){}    protected NavigationEntity(Parcel in) {        name = in.readString();        img = in.readInt();        packageName = in.readString();        className = in.readString();        isUp = in.readByte() != 0;    }    @Override    public void writeToParcel(Parcel dest, int flags) {        dest.writeString(name);        dest.writeInt(img);        dest.writeString(packageName);        dest.writeString(className);        dest.writeByte((byte) (isUp ? 1 : 0));    }    @Override    public int describeContents() {        return 0;    }    public static final Creator<NavigationEntity> CREATOR = new Creator<NavigationEntity>() {        @Override        public NavigationEntity createFromParcel(Parcel in) {            return new NavigationEntity(in);        }        @Override        public NavigationEntity[] newArray(int size) {            return new NavigationEntity[size];        }    };    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public int getImg() {        return img;    }    public void setImg(int img) {        this.img = img;    }    public boolean isUp() {        return isUp;    }    public void setUp(boolean up) {        isUp = up;    }    public String getClassName() {        return className;    }    public void setClassName(String className) {        this.className = className;    }    public String getPackageName() {        return packageName;    }    public void setPackageName(String packageName) {        this.packageName = packageName;    }}